// Renderer
var renderer = new THREE.WebGLRenderer({ alpha: true });
renderer.setSize(window.innerWidth, window.innerHeight);
renderer.autoClear = false;
document.body.appendChild(renderer.domElement);

// Icosahedron Scene
// Icosahedron Scene
var icosahedronRenderer = new THREE.WebGLRenderer({ alpha: true });
var icosahedronContainer = document.getElementById("dodo");
icosahedronRenderer.setSize(160, 160);
icosahedronContainer.appendChild(icosahedronRenderer.domElement);

var scene = new THREE.Scene();
var camera = new THREE.PerspectiveCamera(75, 160 / 160, 0.1, 1000);
camera.position.z = 1.7;

var icosahedronGeometry = new THREE.IcosahedronGeometry(1);
var wireframeMaterial = new THREE.MeshBasicMaterial({
	color: 0xffffff,
	wireframe: true,
});
var icosahedron = new THREE.Mesh(icosahedronGeometry, wireframeMaterial);
scene.add(icosahedron);

var mouseIco = new THREE.Vector2();
var target = new THREE.Vector2();
var windowHalf = new THREE.Vector2(
	window.innerWidth / 2,
	window.innerHeight / 2
);

function onMouseOrTouchMove(event) {
	if (event.type == "touchmove") {
		mouseIco.x = event.touches[0].clientX - windowHalf.x;
		mouseIco.y = event.touches[0].clientY - windowHalf.y;
	} else {
		mouseIco.x = event.clientX - windowHalf.x;
		mouseIco.y = event.clientY - windowHalf.y;
	}
}

window.addEventListener("mousemove", onMouseOrTouchMove, false);
window.addEventListener("touchmove", onMouseOrTouchMove, false);

// Grid Scene
var gridRenderer = new THREE.WebGLRenderer({ alpha: true });
var gridContainer = document.getElementById("gridContainer");
gridRenderer.setSize(window.innerWidth, window.innerHeight);
gridContainer.appendChild(gridRenderer.domElement);

var backgroundScene = new THREE.Scene();
var backgroundCamera = new THREE.OrthographicCamera(
	window.innerWidth / -2,
	window.innerWidth / 2,
	window.innerHeight / 2,
	window.innerHeight / -2,
	1,
	1000
);
backgroundCamera.position.z = 500;

var gridPoints = [];
var geometry = new THREE.Geometry();
var gridWidth = 100,
	gridHeight = 100,
	spacing = 10;

for (var i = 0; i < gridWidth; i++) {
	for (var j = 0; j < gridHeight; j++) {
		var vertex = new THREE.Vector3(
			i * spacing - (gridWidth * spacing) / 2,
			j * spacing - (gridHeight * spacing) / 2,
			0
		);
		geometry.vertices.push(vertex);
		gridPoints.push(vertex);
	}
}

var material = new THREE.PointsMaterial({ color: 0xffffff, size: 1 }); // Set color to white
var points = new THREE.Points(geometry, material);
backgroundScene.add(points);

var mouseGrid = new THREE.Vector2();

function onMouseMove(event) {
	// Normalize mouse position from -1 to 1
	mouseGrid.x = (event.clientX / window.innerWidth) * 2 - 1;
	mouseGrid.y = -(event.clientY / window.innerHeight) * 2 + 1;

	// Update points based on mouse proximity
	for (var i = 0; i < gridPoints.length; i++) {
		var point = gridPoints[i];
		var mousePoint = new THREE.Vector2(point.x, point.y);
		var distance = mousePoint.distanceTo(mouseGrid) * 400;
		if (distance < 50) {
			point.z = (50 - distance) * 5; // Move point towards camera based on proximity
		} else {
			point.z = 0; // Reset point if mouse is not close
		}
	}
	geometry.verticesNeedUpdate = true; // Update vertices in the geometry
}

window.addEventListener("mousemove", onMouseMove, false);

window.addEventListener("resize", function () {
	var width = window.innerWidth;
	var height = window.innerHeight;
	renderer.setSize(width, height);
	camera.aspect = width / height;
	camera.updateProjectionMatrix();
	backgroundCamera.left = width / -2;
	backgroundCamera.right = width / 2;
	backgroundCamera.top = height / 2;
	backgroundCamera.bottom = height / -2;
	backgroundCamera.updateProjectionMatrix();
});

function animate() {
	requestAnimationFrame(animate);

	target.x = (1 - mouseIco.x) * 0.003;
	target.y = (1 - mouseIco.y) * 0.003;

	icosahedron.rotation.x += 0.05 * (target.y - icosahedron.rotation.x);
	icosahedron.rotation.y += 0.05 * (target.x - icosahedron.rotation.y);

	icosahedronRenderer.render(scene, camera);
	gridRenderer.render(backgroundScene, backgroundCamera);
}

animate();
